import React from "react";
import PropTypes from "prop-types";
import { withStyles } from "@material-ui/core/styles";
import AppBar from "@material-ui/core/AppBar";
import Toolbar from "@material-ui/core/Toolbar";
import IconButton from "@material-ui/core/IconButton";
import Fab from "@material-ui/core/Fab";
import MenuIcon from "@material-ui/icons/Menu";
import AddIcon from "@material-ui/icons/Add";
import SearchIcon from "@material-ui/icons/Search";
import MoreIcon from "@material-ui/icons/MoreVert";
import SwipeableDrawer from "@material-ui/core/SwipeableDrawer";
import List from "@material-ui/core/List";
import Divider from "@material-ui/core/Divider";
import ListItem from "@material-ui/core/ListItem";
import ListItemIcon from "@material-ui/core/ListItemIcon";
import ListItemText from "@material-ui/core/ListItemText";
import { Home, Group, Flag, ExitToApp } from "@material-ui/icons";
import { Link } from "react-router-dom";
import ls from "local-storage";
import Dialog from '@material-ui/core/Dialog';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import DialogContentText from '@material-ui/core/DialogContentText';
import DialogTitle from '@material-ui/core/DialogTitle';
import Button from '@material-ui/core/Button';

const styles = theme => ({
  text: {
    paddingTop: theme.spacing.unit * 2,
    paddingLeft: theme.spacing.unit * 2,
    paddingRight: theme.spacing.unit * 2
  },
  paper: {
    paddingBottom: 50
  },
  list: {
    marginBottom: theme.spacing.unit * 2
  },
  subHeader: {
    backgroundColor: theme.palette.background.paper
  },
  appBar: {
    top: "auto",
    bottom: 0
  },
  toolbar: {
    alignItems: "center",
    justifyContent: "space-between"
  },
  fabButton: {
    position: "absolute",
    zIndex: 1,
    top: -30,
    left: 0,
    right: 0,
    margin: "0 auto"
  }
});

class BottomAppBar extends React.Component {
  state = {
    navigationMenuOpen: false,
    logoutDialogOpen: false
  };

  updateState = (type, value) => {
    const currentState = this.state;
    this.state[type] = value;
    this.setState(currentState);
  };

  toggleDrawer = open => () => {
    this.updateState("navigationMenuOpen", open);
  };

  
  toggleLogoutDialog = () => {
    this.updateState("logoutDialogOpen", !this.state.logoutDialogOpen);
  };

  logoutUser = ()=> {
    this.toggleLogoutDialog();
    this.props.logout();
  }

  render() {
    const { classes } = this.props;
    const notLoggedList = (
      <List>
        <ListItem button key={"Homepage"} component={Link} to='/' selected={window.location.pathname === "/"}>
          <ListItemIcon>
            <Home />
          </ListItemIcon>
          <ListItemText primary={"Homepage"} />
        </ListItem>

        <ListItem button key={"Login"} component={Link} to='/login' selected={window.location.pathname === "/login"}>
          <ListItemIcon>
            <Group />
          </ListItemIcon>
          <ListItemText primary={"Login"} />
        </ListItem>

        <ListItem button key={"Flag"} selected={window.location.pathname === "/liveGameList"}>
          <ListItemIcon>
            <Flag />
          </ListItemIcon>
          <ListItemText primary={"Partite in corso"} />
        </ListItem>
      </List>

    );
    const username = this.props.logged ? ls.get("user").name : "";

    const fullList = (
      <div className={classes.fullList}>
        <List>
          <ListItem button key={username}>
            <ListItemText primary={username} />
          </ListItem>
        </List>
        <Divider />
        <List>
          <ListItem button key={"Homepage"} component={Link} to='/' selected={window.location.pathname === "/"}>
            <ListItemIcon>
              <Home />
            </ListItemIcon>
            <ListItemText primary={"Homepage"} />
          </ListItem>

          <ListItem button key={"Teams"}>
            <ListItemIcon>
              <Group />
            </ListItemIcon>
            <ListItemText primary={"Le tue squadre"} />
          </ListItem>

          <ListItem button key={"Flag"} selected={window.location.pathname === "/liveGameList"}>
            <ListItemIcon>
              <Flag />
            </ListItemIcon>
            <ListItemText primary={"Partite in corso"} />
          </ListItem>

          <ListItem button key={"Logout"} onClick={this.toggleLogoutDialog}>
            <ListItemIcon>
              <ExitToApp />
            </ListItemIcon>
            <ListItemText primary={"Esci"} />
          </ListItem>
        </List>
      </div>
    );
    return (
      <div>
        {/* navigation menu */}
        <SwipeableDrawer
          anchor="bottom"
          open={this.state.navigationMenuOpen}
          onClose={this.toggleDrawer(false)}
          onOpen={this.toggleDrawer(true)}
        >
          <div
            tabIndex={0}
            role="button"
            onClick={this.toggleDrawer(false)}
            onKeyDown={this.toggleDrawer(false)}
          >
            {this.props.logged ? fullList : notLoggedList}
          </div>
        </SwipeableDrawer>

        {/* logout dialog */}
        <Dialog
          open={this.state.logoutDialogOpen}
          onClose={this.toggleLogoutDialog}
          aria-labelledby="alert-dialog-title"
          aria-describedby="alert-dialog-description"
        >
          <DialogTitle id="alert-dialog-title">{"Logout"}</DialogTitle>
          <DialogContent>
            <DialogContentText id="alert-dialog-description">
              Sei sicuro di voler effettuare il logout?
            </DialogContentText>
          </DialogContent>
          <DialogActions>
            <Button onClick={this.toggleLogoutDialog} color="primary">
              No
            </Button>
            <Button onClick={this.logoutUser} color="primary" autoFocus>
              Si
            </Button>
          </DialogActions>
        </Dialog>


        {/* bottom app bar */}
        <AppBar position="fixed" color="primary" className={classes.appBar}>
          <Toolbar className={classes.toolbar}>
            <IconButton
              color="inherit"
              aria-label="Open drawer"
              onClick={this.toggleDrawer(true)}
            >
              <MenuIcon />
            </IconButton>
            <Fab
              color="secondary"
              aria-label="Add"
              className={classes.fabButton}
            >
              <AddIcon />
            </Fab>
            <div>
              <IconButton color="inherit">
                <SearchIcon />
              </IconButton>
              <IconButton color="inherit">
                <MoreIcon />
              </IconButton>
            </div>
          </Toolbar>
        </AppBar>
      </div>
    );
  }
}

BottomAppBar.propTypes = {
  classes: PropTypes.object.isRequired
};

export default withStyles(styles)(BottomAppBar);
